    /**
     * 链表两两反转
     * @param node
     * @return
     */
    public static Node revertNode(Node node){
        Node head = node;
        while(head.next != null && head.next.next != null){
            // 开始反转
            Node next1 = head.next;
            Node next2 = next1.next;
            head.next = next2;
            next1.next = next2.next;
            next2.next = next1;
            // dummy 指针前移
            head = next1;
        }
        return head;
